namespace DigitBridge.CommerceCentral.ERPDb.Tests.Integration
{
    public partial class MiscInvoiceDataTests
    {
        public const int MasterAccountNum = 10001;
        public const int ProfileNum = 10001;
        public static MiscInvoiceData GetFakerMiscInvoiceData(IDataBaseFactory dbFactory)
        {
            var data = GetFakerData();
            data.MiscInvoiceHeader.MasterAccountNum = MasterAccountNum;
            data.MiscInvoiceHeader.ProfileNum = ProfileNum;
            data.MiscInvoiceHeader.MiscInvoiceNumber = NumberGenerate.Generate();
            data.MiscInvoiceHeader.Balance = data.MiscInvoiceHeader.TotalAmount;
            return data;
        }

        public static MiscInvoiceData SaveFakerMiscInvoice(IDataBaseFactory dbFactory, MiscInvoiceData data = null)
        {
            var srv = new MiscInvoiceService(dbFactory);
            srv.Add();

            var mapper = srv.DtoMapper;
            if (data == null)
                data = GetFakerMiscInvoiceData(dbFactory);
            var dto = mapper.WriteDto(data, null);
            var success = srv.Add(dto);

            Assert.True(success, srv.Messages.ObjectToString());

            return srv.Data;
        }
    }
