        public bool GetShippingCodes(ShippingCodesPayload payload)
        {
            int masterAccountNum = payload.MasterAccountNum;
            int profileNum = payload.ProfileNum;
            try
            {
                payload.ShippingCodess = GetShippingCodesData(masterAccountNum, profileNum);
                payload.Success = true;
            }
            catch (Exception ex)
            {
                payload.ReturnError(ex.Message);
            }

            return payload.Success;
        }

        public bool UpdateShippingCodes(ShippingCodesPayload payload)
        {
            int masterAccountNum = payload.MasterAccountNum;
            int profileNum = payload.ProfileNum;
            var list = GetShippingCodesData(masterAccountNum, profileNum);

            try
            {
                foreach (var item in list)
                {
                    if (payload.ShippingCodess.Contains(item))
                    {
                        Update(item);
                        payload.ShippingCodess.Remove(item);
                    }
                    else
                    {
                        Delete(item.ShippingCodes.RowNum.ToInt());
                    }
                }

                foreach (var item in payload.ShippingCodess)
                {
                    item.ShippingCodes.MasterAccountNum = masterAccountNum;
                    item.ShippingCodes.ProfileNum = profileNum;
                    Add(item);
                }

                payload.Success = true;
                payload.ShippingCodess = GetShippingCodesData(masterAccountNum, profileNum);
            }
            catch (Exception ex)
            {
                payload.ReturnError(ex.Message);
            }

            return payload.Success;
        }
