                if (dto.InvoiceItems != null && dto.InvoiceItems.Count > 0)
                {
                    foreach (var detailItem in dto.InvoiceItems)
                        detailItem.InvoiceItemsUuid = null;
                }
            }
            IsValid = isValid;
            return isValid;
        }
