        public static async Task<StringBuilder> GetWMSOrderShipmentListAsync(int masterAccountNum, int profileNum, IList<string> shipmentIDs)
        {
            var sql = $@"
select 
 ep.ProcessDate
,ep.ProcessUuid as ShipmentID
,ep.ProcessStatus
,COALESCE(epps.text, '') as ProcessStatusText
,ep.EventMessage
,ep.LastUpdateDate
,ep.UpdateDateUtc
,ep.EnterDateUtc
,ep.EnterBy
,ep.UpdateBy
from EventProcessERP ep
JOIN @shipmentIDs shipmentID on shipmentID.item=ep.ProcessUuid
LEFT JOIN @ProcessStatus epps on epps.num=ep.ProcessStatus
