                    }
                    else
                    {
                        if (dto.ProductExt.HasSKU)
                            isValid = await InventoryServiceHelper.ExistNumberInExtAsync(dto.ProductExt.SKU, pl.MasterAccountNum, pl.ProfileNum);
                        else
                            isValid = false;
                    }
                }
                if (!isValid)
                    AddError($"Data not found.");
            }
            IsValid = isValid;
            return isValid;
