        private void ClearInventoryLogByLogUuid(string logUuid)
        {
            UpdateInventoryInStock(logUuid, -1);
            DeleteInventoryLogByLogUuid(logUuid);
        }

        private async Task ClearInventoryLogByLogUuidAsync(string logUuid)
        {
