        /// <summary>
        /// Validate dto.
        /// </summary>
        /// <param name="dto"></param>
        /// <param name="dbFactory"></param>
        /// <param name="processingMode"></param>
        /// <returns></returns>
        public virtual bool Validate(WarehouseTransferDataDto dto, ProcessingMode processingMode = ProcessingMode.Edit)
        {
            var isValid = true;
            if (dto is null)
            {
                isValid = false;
                AddError($"Data not found");
            }
            if (processingMode == ProcessingMode.Add)
            {
                //for Add mode, always reset uuid
                dto.WarehouseTransferHeader.WarehouseTransferUuid = null;
                if (string.IsNullOrEmpty(dto.WarehouseTransferHeader.FromWarehouseCode) && string.IsNullOrEmpty(dto.WarehouseTransferHeader.FromWarehouseUuid))
                {
                    isValid = false;
                    AddError($"FromWarehouseCode&FromWarehouseUuid at lease one");
                }
                if (string.IsNullOrEmpty(dto.WarehouseTransferHeader.ToWarehouseCode) && string.IsNullOrEmpty(dto.WarehouseTransferHeader.ToWarehouseUuid))
                {
                    isValid = false;
                    AddError($"ToWarehouseCode&ToWarehouseUuid at lease one");
                }
                if (dto.WarehouseTransferItems != null && dto.WarehouseTransferItems.Count > 0)
                {
                    var list = dto.WarehouseTransferItems.Select(x => new StringArray() { Item0 = x.SKU, Item1 = x.FromWarehouseCode }).Distinct().ToList();
                    var list2 = dto.WarehouseTransferItems.Select(x => new StringArray() { Item0 = x.SKU, Item1 = x.ToWarehouseCode }).Distinct().ToList();
                    list = list.Union(list2).ToList();
                    using (var trx = new ScopedTransaction(dbFactory))
                    {
                        list = InventoryServiceHelper.ExistInventoryBySkuWithWarehouseCodes(list, dto.WarehouseTransferHeader.MasterAccountNum.ToInt(), dto.WarehouseTransferHeader.ProfileNum.ToInt());
                        if (list.Count > 0)
                        {
                            foreach (var item in list)
                            {
                                AddError($"SKU:{item.Item0} ,WarehouseCode:{item.Item1} data not found");
                            }
                            isValid = false;
                        }
                    }
                    foreach (var detailItem in dto.WarehouseTransferItems)
                    {
                        detailItem.WarehouseTransferItemsUuid = null;
                    }
                }

            }
            else if (processingMode == ProcessingMode.Edit)
            {
                if (dto.WarehouseTransferHeader.RowNum.IsZero())
                {
                    isValid = false;
                    AddError("WarehouseTransferHeader.RowNum is required.");
                }
                // This property should not be changed.
                dto.WarehouseTransferHeader.MasterAccountNum = null;
                dto.WarehouseTransferHeader.ProfileNum = null;
                dto.WarehouseTransferHeader.DatabaseNum = null;
                dto.WarehouseTransferHeader.WarehouseTransferUuid = null;
                // TODO 
                //dto.SalesOrderHeader.OrderNumber = null;
                if (dto.WarehouseTransferItems != null && dto.WarehouseTransferItems.Count > 0)
                {
                    foreach (var detailItem in dto.WarehouseTransferItems)
                    {
                        detailItem.WarehouseTransferItemsUuid = null;
                        detailItem.ProductUuid = null;
                        detailItem.FromWarehouseCode = null;
                        detailItem.FromInventoryUuid = null;
                        detailItem.FromWarehouseUuid = null;
                        detailItem.ToWarehouseCode = null;
                        detailItem.ToInventoryUuid = null;
                        detailItem.ToWarehouseUuid = null;
                    }
                }
            }
            IsValid=isValid;
            return isValid;
        }
